int isPrime(int n) {
    if (n <= 1) return 0;
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0) return 0;
    }
    return 1;
}

int findHighestPrime(int arr[], int size) {
    int highestPrime = -1;
    for (int i = 0; i < size; i++) {
        if (isPrime(arr[i]) && arr[i] > highestPrime) {
            highestPrime = arr[i];
        }
    }
    return highestPrime;
}

int largestDecreasingSequence(int arr[], int size) {
    int maxLength = 1;
    int currentLength = 1;
    for (int i = 1; i < size; i++) {
        if (arr[i] < arr[i - 1]) {
            currentLength++;
        } else {
            if (currentLength > maxLength) {
                maxLength = currentLength;
            }
            currentLength = 1;
        }
    }
    if (currentLength > maxLength) {
        maxLength = currentLength;
    }
    return maxLength;
}

